const fs = require('fs');

const path = require('path');

const flatDir = (src,dist) => {
    if(fs.existsSync(src)){
        if(fs.statSync(src).isDirectory()){
            fs.appendFileSync(dist,path.basename(src))
            let list = fs.readdirSync(src);
            list.forEach(item => {
                flatDir(path.join(src,item),dist)
            })    
        }else{
            fs.appendFileSync(dist,path.basename(src))
        }
    }else{
        console.log("不存在")
    }
}

module.exports = flatDir