const path = require('path');
const fs = require('fs');


const removeDir = (url) => {
    // rmdirSync  unlinkSync

    if(fs.existsSync(url)){
        //文件  文件夹
        if(fs.statSync(url).isDirectory()){
            let list = fs.readdirSync(url);
            list.forEach(item => {
                removeDir(path.join(url,item))   
            })
            fs.rmdirSync(url);
        }else{
            fs.unlinkSync(url);
        }
    }else{
        console.error("当前路径不存在")
    }
}

module.exports = removeDir

